ruleSearch.coverUrl:
tag.td.0@a@href@js:
var cover=new String(result);
var a = cover.match(/\/(\d+)_(\d+)\/$/);
if(a) {
  cover = "/files/article/image/" + a[1] + "/" + a[2] + "/" + a[2] + "s.jpg";
}
else {
    cover = "";
}
cover;

