ruleSearch.coverUrl:
a@href@js:
var id = new String(result);
var a = id.match(/\/(\d+)\//);
if(a) {
    id = a[1];
}
else {
    id = 0;
}
var xid = Math.floor(id/1000);
"/image/" + xid + "/" + id + "/" + id + "s.jpg";

