ruleFindList:
class.keys@tag.a|class.common-bookele|class.last_li<js>
	if(baseUrl.match("#hot$")){
		r=result.clone();
		r.empty();
		a=result;
		for(i=0;i<a.size();i++){
			b=a.get(i).attr("href");
			b=baseUrl.replace(/^(http:\/\/[^\/]+)\/.*/,"$1"+b);
			d=java.ajax(b);
			java.setContent(d);
			c=java.getElements("class.common-bookele");
			if(c.size()>0) r.addAll(c)
		}
		result = r;
	}
  result
</js>
